import React, { useState } from 'react'
import { FaShoppingCart } from "react-icons/fa";
import Order from './Order';
import { BrowserRouter as Router, Switch, Route, Link } from 'react-router-dom';

const showOrders = (props) => {
  let summa = 0
  props.orders.forEach(el => summa += Number.parseFloat(el.price))
  return(
    <div>
      {props.orders.map(el => (
        <Order onDelete={props.onDelete} key={el.id} item={el} />
      ))}
      <p className='summa'>Сумма : {new Intl.NumberFormat().format(summa)}$</p>
    </div>
  )
}

const showNothing = () => {
  return(
    <div className='empty'>
      <h2>Нічого немає</h2>
    </div>
  )
}

export default function Header(props) {
  let [cardOpen, setCardOpen] = useState(false)
  return (
    <header>
      
      <div>
        <span className='logo'>Krispy Kreme</span>
        <Router>
        <ul className='nav'>
          <li>
            <Link to = '/src/components/About.js'>Про нас</Link>
            </li>
          <li><Link to = '/src/components/Contacts.js'>Контакти</Link></li>
          <li><Link to = '/src/components/User.js'>Користувач</Link></li>
        </ul>
        </Router>
      <FaShoppingCart onClick={() => setCardOpen(cardOpen = !cardOpen)}
        className={`shop-button ${cardOpen && 'active'}`} />

      {cardOpen && (
        <div className='shop-card'>
          {props.orders.length > 0 ?
            showOrders(props) : showNothing()}
        </div>
      )}

    </div>
    <div className='presentation'></div>
    </header>
  )
}
